INSERT INTO `dk_article` (`id`, `cat_id`, `title`, `subtitle`, `thumb`, `video`, `content`, `note`, `create_at`, `enabled`, `sort`, `click`, `istj`, `istwtj`, `link`) VALUES
(49, 2, '心理咨询中心召开心理健康教研室第一次教研会议', '', '', NULL, '<p>					</p><p><br/></p><p><br/></p><p><br/></p><p style="margin: 20.86px 0px; text-align: center;"><strong><span style="margin: 0px; color: rgb(103, 106, 108); font-family: 方正小标宋简体; font-size: 22pt; font-weight: bold;"><span style="font-family:方正小标宋简体">心理咨询中心召开心理健康教研室第一次教研会议</span></span></strong><span style="margin: 0px; font-family: 方正小标宋简体; font-size: 22pt;"></span></p><p style="margin: 0px; line-height: 28pt; text-indent: 32pt;"><span style="margin: 0px; color: rgb(103, 106, 108); font-family: 仿宋_GB2312; font-size: 16pt;">2013年</span><span style="margin: 0px; color: rgb(103, 106, 108); font-family: 仿宋_GB2312; font-size: 16pt;">9月22日下午17时，心理咨询中心在JX7109组织召开了首次心理健康教育教研室教研活动，活动由学生处王国英副处长主持，学生处处长朱焕立、副处长刘许亮，心理咨询中心专兼职教师、各系心理指导教师代表参加了教研会议。</span><span style="margin: 0px; font-family: 仿宋_GB2312; font-size: 16pt;"></span></p><p style="margin: 0px; line-height: 28pt; text-indent: 32pt;"><span style="margin: 0px; color: rgb(103, 106, 108); font-family: 仿宋_GB2312; font-size: 16pt;"><span style="font-family:仿宋_GB2312">会上，心理咨询中心主任郭豪杰针对本学期首次开设《大学生心理健康教育课程》必修课的相关事宜，如课程设计、教材、课件进行了简单的部署。全体老师一起就心理健康教育教学工作进行了研讨，本学期心理健康教育课程共</span>12各学时，以讲座的形式面对全体大一新生讲解心理健康基础知识、大学生活适应、人际关系、自我认知、情绪情感、恋爱困扰等。</span><span style="margin: 0px; font-family: 仿宋_GB2312; font-size: 16pt;"></span></p><p style="margin: 0px; line-height: 28pt; text-indent: 32pt;"><span style="margin: 0px; color: rgb(103, 106, 108); font-family: 仿宋_GB2312; font-size: 16pt;"><span style="font-family:仿宋_GB2312">朱焕立处长对全体任课教师提出高标准、严要求，并结合自己上课的经验针对上课考勤、期末考试、课堂气氛、师生互动等内容对大家进行了一次简单的培训。</span></span><span style="margin: 0px; font-family: 仿宋_GB2312; font-size: 16pt;"></span></p><p style="margin: 0px; line-height: 28pt; text-indent: 32pt;"><span style="margin: 0px; color: rgb(103, 106, 108); font-family: 仿宋_GB2312; font-size: 16pt;"><span style="font-family:仿宋_GB2312">本次研讨活动为提高我院心理健康教育工作的质量起到了重要作用，会议决定每周举行一次教研活动，每一次有</span>1-2名教师进行说课活动，帮助年青教师提高授课技巧和能力。</span><span style="margin: 0px; font-family: 仿宋_GB2312; font-size: 16pt;"></span></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p>					</p>', '0', '1380211200', 1, 0, 0, 0, 0, ''),
(50, 1, '专兼职咨询师总介绍', '', '', NULL, '<p>					</p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p style="text-align: center; color: rgb(0, 0, 0); text-transform: none; text-indent: 0px; letter-spacing: normal; font-family: Simsun; font-size: medium; font-style: normal; font-weight: normal; word-spacing: 0px; white-space: normal; orphans: 2; widows: 2; -webkit-text-stroke-width: 0px;"><span style="font-size: 36px;"><strong>黄河水利职业技术学院</strong></span></p><p style="color: rgb(0, 0, 0); text-transform: none; text-indent: 0px; letter-spacing: normal; font-family: Simsun; font-size: medium; font-style: normal; font-weight: normal; word-spacing: 0px; white-space: normal; orphans: 2; widows: 2; -webkit-text-stroke-width: 0px;"><span style="font-size: 36px;"><strong><br/></strong></span></p><p style="text-align: center; color: rgb(0, 0, 0); text-transform: none; text-indent: 0px; letter-spacing: normal; font-family: Simsun; font-size: medium; font-style: normal; font-weight: normal; word-spacing: 0px; white-space: normal; orphans: 2; widows: 2; -webkit-text-stroke-width: 0px;"><span style="font-size: 36px;"><strong>心理健康教育中心专兼职咨询师介绍</strong></span></p><p style="color: rgb(0, 0, 0); font-family: Simsun; font-size: medium; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px;"><span style="font-size:24px"><strong><br/></strong></span></p><p style="color: rgb(0, 0, 0); font-family: Simsun; font-size: medium; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px;"><span style="font-family: 楷体,楷体_GB2312,SimKai; font-size: 24px;">校内专职咨询师：郭豪杰、王超、刘娟</span></p><p style="color: rgb(0, 0, 0); font-family: Simsun; font-size: medium; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px;"><span style="font-family: 楷体,楷体_GB2312,SimKai; font-size: 24px;">校内兼职咨询师：杨瑄、朱孟昀、赵诗捷、王<span style="font-family: 楷体,楷体_GB2312,SimKai; font-size: 24px;">瑶、高维</span>峰、<span style="display: inline !important; float: none; background-color: transparent; color: rgb(0, 0, 0); font-family: 楷体,楷体_GB2312,SimKai; font-size: 24px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;">曹林英、</span>李冰</span></p><p style="color: rgb(0, 0, 0); font-family: Simsun; font-size: medium; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px;"><span style="font-family: 楷体,楷体_GB2312,SimKai; font-size: 24px;">土木与交通工程学院二级心理辅导站兼职咨询师：靳玉娟、王永强</span></p><p style="color: rgb(0, 0, 0); font-family: Simsun; font-size: medium; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px;"><span style="font-family: 楷体,楷体_GB2312,SimKai; font-size: 24px;">财经系二级心理辅导站兼职咨询师：杨宁</span></p><p style="color: rgb(0, 0, 0); font-family: Simsun; font-size: medium; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px;"><span style="font-family: 楷体,楷体_GB2312,SimKai; font-size: 24px;">沙盘咨询师：郭豪杰、王超、刘娟、<span style="display: inline !important; float: none; background-color: transparent; color: rgb(0, 0, 0); font-family: 楷体,楷体_GB2312,SimKai; font-size: 24px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;">朱孟昀、赵诗捷</span></span></p><p style="color: rgb(0, 0, 0); font-family: Simsun; font-size: medium; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px;"><span style="font-size:24px"></span></p><p style="color: rgb(0, 0, 0); font-family: Simsun; font-size: medium; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px;"><span style="font-size:24px"><strong><br/></strong></span></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p>					</p>', '0', '1527177600', 1, 0, 0, 0, 0, ''),
(52, 5, '心理咨询中心教师为自动化系学生做恋爱心理讲座', '', '', NULL, '<p>					</p><h1 style="margin: 5px 0px 10px; text-align: center;"><strong><span style="margin: 0px; color: rgb(51, 51, 51); font-family: 方正小标宋简体; font-size: 22pt; font-weight: bold;"><span style="font-family:方正小标宋简体">心理咨询中心教师为自动化系学生做恋爱心理讲座</span></span></strong><strong><span style="margin: 0px; color: rgb(51, 51, 51); font-family: 宋体; font-size: 18pt; font-weight: bold;"></span></strong></h1><p style="margin: 0px; text-align: left; line-height: 28pt; text-indent: 32pt;"><span style="margin: 0px; color: rgb(103, 106, 108); font-family: 仿宋_GB2312; font-size: 16pt;">3月11日晚，JX2202教室座无虚席不时传出阵阵掌声，心理咨询中心倪凤琨老师受自动化系女工部邀请为该系学生义务作了一场“透视恋爱、把握自我”的心理讲座。</span><span style="margin: 0px; font-family: 仿宋_GB2312; font-size: 16pt;"></span></p><p style="margin: 0px; text-align: left; line-height: 28pt; text-indent: 32pt;"><span style="margin: 0px; color: rgb(103, 106, 108); font-family: 仿宋_GB2312; font-size: 16pt;"><span style="font-family:仿宋_GB2312">倪老师根据多年从事心理健康教育工作的经验，从学生的身边案例入手，深入浅出讲解了</span>“谈恋爱前应该具备的心理条件，如何选择适合自己的恋人，遇到不合适的求爱者怎样处理，如何对待恋爱中的磨合期，如何尽快走出失恋的阴影”等问题，重点诠释了青年学生应当正确持有的恋爱观、性欲观。讲座以幽默亲切的语言、生动的案例赢得了同学们的阵阵掌声。讲座结束后，学生们迟迟不愿离去，纷纷发出“继续讲下去”的呼声。倪凤琨老师后来根据学生需要安排了自由提问环节，并把心理咨询中心的联系方式写在黑板上，以供个别学生预约心理咨询。</span><span style="margin: 0px; font-family: 仿宋_GB2312; font-size: 16pt;"></span></p><p style="margin: 5px 0px 10px; text-align: left; line-height: 28pt; text-indent: 32pt;"><span style="margin: 0px; color: rgb(103, 106, 108); font-family: 仿宋_GB2312; font-size: 16pt;"><span style="font-family:仿宋_GB2312">通过本次讲座，很多同学们端正了恋爱观、性欲观和失恋观。同学们表示要在今后的学习和生活中，认真把握自我，珍惜美好的大学学习时光，正确对待爱情和失恋，正确处理恋爱和学习的关系。</span></span><span style="margin: 0px; font-family: 仿宋_GB2312; font-size: 16pt;"></span></p><p style="margin: 5px 0px 0px; text-align: left; line-height: 28pt; text-indent: 32pt;"><span style="margin: 0px; font-family: 仿宋_GB2312; font-size: 16pt;">&nbsp;</span></p><p style="margin: 0px; line-height: 28pt; text-indent: 32pt;"><span style="margin: 0px; font-family: 仿宋_GB2312; font-size: 16pt;">&nbsp;</span></p><p><br/></p><p>					</p>', '0', '1205424000', 1, 0, 0, 0, 0, '');
INSERT INTO `dk_article` (`id`, `cat_id`, `title`, `subtitle`, `thumb`, `video`, `content`, `note`, `create_at`, `enabled`, `sort`, `click`, `istj`, `istwtj`, `link`) VALUES
(9, 4, '《大学生心理健康教育》教学大纲', '', '', NULL, '<p>					</p><p><br/></p><p style="margin: 20.8px 0px; text-align: center; line-height: 150%;"><strong><span style="margin: 0px; line-height: 150%; font-family: 黑体; font-size: 18pt; font-weight: bold;"><span style="font-family:黑体">《大学生心理健康教育》教学大纲</span></span></strong><strong><span style="margin: 0px; line-height: 150%; font-family: 黑体; font-size: 18pt; font-weight: bold;"></span></strong></p><p style="margin: 0px; line-height: 150%; text-indent: 28pt;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"><span style="font-family:仿宋_GB2312">课程代码：</span></span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28pt;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"><span style="font-family:仿宋_GB2312">课程名称：《大学生心理健康教育》</span></span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28pt;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"><span style="font-family:仿宋_GB2312">学时学分：</span>12/1</span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28pt;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"><span style="font-family:仿宋_GB2312">课程类别：公共基础课</span></span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28pt;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"><span style="font-family:仿宋_GB2312">授课对象：全院一年级各专业</span></span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28pt;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"><span style="font-family:仿宋_GB2312">开课单位：学生处心理咨询中心</span></span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28pt;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"><span style="font-family:仿宋_GB2312">教</span> &nbsp;&nbsp;&nbsp;<span style="font-family:仿宋_GB2312">材：《大学生心理健康教育与素质拓展训练教程》</span></span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><h1 style="margin: 0px; text-indent: 27pt;"><strong><span style="margin: 0px; color: rgb(0, 0, 0); font-family: 仿宋_GB2312; font-size: 14pt; font-weight: bold;"><span style="font-family:仿宋_GB2312">一、课程性质与教学目标</span></span></strong><strong><span style="margin: 0px; color: rgb(0, 0, 0); font-family: 仿宋_GB2312; font-size: 14pt; font-weight: bold;"></span></strong></h1><p style="margin: 0px; line-height: 150%; text-indent: 28pt;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"><span style="font-family:仿宋_GB2312">大学生心理健康教育课程是是集知识传授、心理体验与行为训练为一体的面向全院的公共必修课程。该课程的主要任务是系统、重点地介绍大学生心理健康教育的基本理论和基本知识，增强自我心理保健意识和心理危机预防意识，掌握并应用心理健康知识，掌握维护心理健康的基本方法，掌握训练良好心理素质的基本技能，充分地认识到心理健康对成长成才的重要意义，培养自我认知能力、人际沟通能力、自我调节能力，切实提高心理素质，促进学生全面发展。为他们毕业后走上各级各类工作岗位，适应社会的快速变化打下坚实的心理基础。</span></span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28pt;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"><span style="font-family:仿宋_GB2312">通过课程教学，使学生在知识、技能和自我认知三个层面达到以下目标。</span></span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28.1pt; layout-grid-mode: char;"><strong><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt; font-weight: bold;"><span style="font-family:仿宋_GB2312">知识层面：</span></span></strong><strong><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt; font-weight: bold;"></span></strong></p><p style="margin: 0px; line-height: 150%; text-indent: 28pt;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"><span style="font-family:仿宋_GB2312">通过本课程的教学，使学生了解心理学的有关理论和基本概念，明确心理健康的标准及意义，了解大学阶段人的心理发展特征及异常表现，掌握自我调适的基本知识。</span></span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28.1pt;"><strong><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt; font-weight: bold;"><span style="font-family:仿宋_GB2312">技能层面：</span></span></strong><strong><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt; font-weight: bold;"></span></strong></p><p style="margin: 0px; line-height: 150%; text-indent: 28pt;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"><span style="font-family:仿宋_GB2312">通过本课程的教学，使学生掌握自我探索技能，心理调适技能及心理发展技能。如学习发展技能、环境适应技能、压力管理技能、沟通技能、问题解决技能、自我管理技能、人际交往技能和生涯规划技能等。</span></span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28.1pt; layout-grid-mode: char;"><strong><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt; font-weight: bold;"><span style="font-family:仿宋_GB2312">自我认知层面：</span></span></strong><strong><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt; font-weight: bold;"></span></strong></p><p style="margin: 0px; line-height: 150%; text-indent: 28pt; layout-grid-mode: char;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"><span style="font-family:仿宋_GB2312">通过本课程的教学，使学生树立心理健康发展的自主意识，了解自身的心理特点和性格特征，能够对自己的身体条件、心理状况、行为能力等进行客观评价，正确认识自己、接纳自己，在遇到心理问题时能够进行自我调适或寻求帮助，积极探索适合自己并适应社会的生活状态，并且能运用学到的心理学知识帮助周围的同学和同龄人。</span></span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><h1 style="margin: 0px; text-indent: 27pt;"><strong><span style="margin: 0px; color: rgb(0, 0, 0); font-family: 仿宋_GB2312; font-size: 14pt; font-weight: bold;"><span style="font-family:仿宋_GB2312">二、课程教学的基本要求</span></span></strong><strong><span style="margin: 0px; color: rgb(0, 0, 0); font-family: 仿宋_GB2312; font-size: 14pt; font-weight: bold;"></span></strong></h1><p style="margin: 0px; line-height: 150%; text-indent: 28pt;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"><span style="font-family:仿宋_GB2312">通过本课程的学习，使学生了解心理健康教育的价值和意义；理解心理健康的理论；掌握维护心理健康的方法和自我调适的策略；让大学生学会一些维护身体和心理健康的基本技巧与方法，增强自我保护能力，以提高大学生的身心素质。</span></span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28pt;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"><span style="font-family:仿宋_GB2312">对主讲教师来说，必须通晓心理学的基本理论、技术体系，具有丰富的知识经验积累，教学中从青年心理学角度，运用心理学有关知识，探讨成长中青年所面临的种种问题，侧重进行思想政治教育和德育，努力把大学生心理健康教育开展为</span>“德育教育的重要组成部分”、“素质教育的主要途径”，教学风格稳健而又不失幽默风趣、教学内容精心设计而又注重师生互动。</span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28pt;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"><span style="font-family:仿宋_GB2312">对学生而言，全程听讲，认真记录，才可以系统学习心理健康的理论技术；积极思考，大胆提问，更能够体验心理学课程的专业魅力。</span></span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><h1 style="margin: 0px; text-indent: 27pt;"><strong><span style="margin: 0px; color: rgb(0, 0, 0); font-family: 仿宋_GB2312; font-size: 14pt; font-weight: bold;"><span style="font-family:仿宋_GB2312">三、</span><span style="font-family:仿宋_GB2312">主要教学内容</span></span></strong><strong><span style="margin: 0px; color: rgb(0, 0, 0); font-family: 仿宋_GB2312; font-size: 14pt; font-weight: bold;"></span></strong></h1><p style="margin: 0px; line-height: 150%; text-indent: 28pt; layout-grid-mode: char;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"><span style="font-family:仿宋_GB2312">本课程涉及到心理学、教育学、生理学、社会学等多学科的知识，是一门综合性较强的素质教育课程，总体上阐述自我意识、人格心理、情绪情感、人际关系、压力管理与挫折等重要内容。现将教学内容简述如下：</span></span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28.1pt; layout-grid-mode: char;"><strong><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt; font-weight: bold;"><span style="font-family:仿宋_GB2312">第一章：大学生心理健康概述（</span>2课时）</span></strong><strong><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt; font-weight: bold;"></span></strong></p><p style="margin: 0px; line-height: 150%; text-indent: 28pt; layout-grid-mode: char;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"><span style="font-family:仿宋_GB2312">教学目标：通过教学使学生了解大学生心理健康的标准，树立正确的心理健康观念；了解心理咨询的基本概念和功能、心理咨询的内容与类型，建立正确的心理咨询观念以及求助意识；了解常见的大学生心理困惑及异常心理，了解心理疾病，懂得哪些状态可以通过自我调整或心理咨询进行解决，哪些心理疾病需要专业医疗机构诊治。</span></span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28pt; layout-grid-mode: char;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"><span style="font-family:仿宋_GB2312">教学内容：</span></span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28pt; layout-grid-mode: char;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;">1.大学生心理健康的标准和影响大学生心理健康的主要因素；</span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28.5pt;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;">2.大学生心理咨询的意义、特点、内容与类型等；</span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28.5pt;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;">3.大学生常见的心理困惑及异常心理；</span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28.5pt;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;">4.大学生常见的心理疾病及其应对。</span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28.5pt;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"><span style="font-family:仿宋_GB2312">教学重点：正确认识大学生心理健康标准和心理咨询。</span></span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28.5pt;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"><span style="font-family:仿宋_GB2312">教学难点：神经症和心理障碍。</span></span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28.5pt;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"><span style="font-family:仿宋_GB2312">教学方法：课堂讲授、课堂活动、案例分析。</span></span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28.5pt;"><strong><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt; font-weight: bold;"><span style="font-family:仿宋_GB2312">第二章：大学生自我意识与完善（</span>2课时）</span></strong><strong><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt; font-weight: bold;"></span></strong></p><p style="margin: 0px; line-height: 150%; text-indent: 28.1pt;"><strong><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt; font-weight: bold;"><span style="font-family:仿宋_GB2312">教学目标：</span></span></strong><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"><span style="font-family:仿宋_GB2312">通过教学使学生认识自我发展的重要性，了解并掌握自我意识发展的特点，能够识别在自我意识发展过程中出现的偏差及原因，并能够对其进行调适，建立自尊自信的自我意识。</span></span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28.1pt;"><strong><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt; font-weight: bold;"><span style="font-family:仿宋_GB2312">教学内容：</span></span></strong><strong><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt; font-weight: bold;"></span></strong></p><p style="margin: 0px; line-height: 150%; text-indent: 28pt;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;">1.自我意识概述；</span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28pt;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;">2.大学生自我意识发展的特点；</span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28pt;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;">3.大学生自我意识偏差及其调适。</span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28pt;"><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;">4.健康自我意识的培养</span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28.1pt;"><strong><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt; font-weight: bold;"><span style="font-family:仿宋_GB2312">教学重点</span></span></strong><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"><span style="font-family:仿宋_GB2312">：自我心理的调适。</span></span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28.1pt;"><strong><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt; font-weight: bold;"><span style="font-family:仿宋_GB2312">教学难点：</span></span></strong><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"><span style="font-family:仿宋_GB2312">自我意识的结构。</span></span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28.1pt;"><strong><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt; font-weight: bold;"><span style="font-family:仿宋_GB2312">教学方法：</span></span></strong><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"><span style="font-family:仿宋_GB2312">课堂讲授、心理测试、案例分析</span></span><span style="margin: 0px; line-height: 150%; font-family: 仿宋_GB2312; font-size: 14pt;"></span></p><p style="margin: 0px; line-height: 150%; text-indent: 28.1pt;"><span style="margin: 0px; font-family: 仿宋_GB2312; font-size: 14pt; font-weight: bo